class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> breakpoints;
        for(int i = 1; i <n; i++)
            breakpoints.insert(i);
        vector<int> res;
        for(auto q : queries) {
            int x = q[0];
            int y = q[1];
            auto itr = breakpoints.upper_bound(x);
            while(itr != breakpoints.end() && *itr < y) {
                auto curr = itr;
                itr++;
                breakpoints.erase(curr);
            }

            res.push_back(breakpoints.size());
        }
        return res;
    }
};
