class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        BitSet bitSet = new BitSet(n);
        bitSet.flip(0, n);
        int m = queries.length;
        int[] ans = new int[m];
        for (int i = 0; i < m; i++) {
            int[] q = queries[i];
            bitSet.clear(q[0] + 1, q[1]);
            ans[i] = bitSet.cardinality() - 1;
        }
        return ans;
    }
}
