class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> path;
        for(int i=0;i<n;i++){
            path.insert(i);
        }
        vector<int> ans;
        for(auto &q:queries)
        {
            auto beg = path.find(q[0]);
            auto end = path.find(q[1]);
            if(beg != path.end() && end!=path.end()){
                beg++;
                path.erase(beg, end);
            }
            ans.push_back(path.size()-1);
        }
        return ans;
    }
};
