class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> u(n,1),v(n);
        for(int i=0;i<(n-1);i++){
            v[i]=i+1;
        }
        vector<int> ans;
        int steps=n-1;
        for(int i=0;i<queries.size();i++){
            int l=queries[i][0],r=queries[i][1];
            if(u[l]&&u[r]){
                int temp=0;
                while(l<r){
                    l=v[l];
                    temp++;
                    if(l<r){
                        u[l]=0;
                    }
                }
                steps-=(temp-1);
                v[queries[i][0]]=r;
            }
            ans.push_back(steps);
        }
        return ans;
    }
};
