class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> g;
        for (int i = 0; i < n; ++i) {
            g.insert(i);
        }

        vector<int> ret;
        for (const auto& q : queries) {
            int x = q[0], y = q[1];

            for (int i = x + 1; i < y;) {
                if (g.contains(i)) {
                    g.erase(i);
                    ++i;
                } else {
                    auto it = g.lower_bound(i);
                    if (it == g.end()) {
                        i = n + 5;
                    } else {
                        i = *it;
                    }
                }
            }

            ret.push_back((int)g.size() - 1);

        }

        return ret;
    }
};
