class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        int right[n];
        for(int i=0;i<n-1;i++){
            right[i]=i+1;
        }
        int dis=n-1;
        for(auto vec:queries){
            int i=vec[0],j=vec[1];
            int stp=-1;
            while(i<j){
                stp++;
                int temp=i;
                i=right[i];
                right[temp]=max(right[temp],j);
            }
            dis-=stp;
            ans.push_back(dis);
        }
        return ans;
    }
};
