using i64 = long long;
using Info = array<int, 2>;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for (int i = 0; i < n; i++) {
            s.insert(i);
        }
        vector<int> ans;
        for (const auto &qr : queries) {
            int u = qr[0];
            int v = qr[1];
            vector<int> del;
            for (auto it = s.upper_bound(u); it != s.end() && *it < v; it++) {
                del.push_back(*it);
            }
            for (int x : del) {
                s.erase(x);
            }
            ans.push_back(s.size() - 1);
        }
        return ans;
    }
};

