class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        map<int,int>mp;
        set<int>st;
        int sol = n-1;
        vt<int>res;
        rep(i , 0 , q.size()){
            int u = q[i][0];
            int v = q[i][1];

            auto it = st.upper_bound(u);

            if(it != st.begin() && st.size()){
              it --;
              if(  (mp[*it]) >= v){
                res.push_back(sol);
                continue;
              }
            }
            set<int>temp;
            it =  st.lower_bound(u);
            if(it != st.end()){
                while(it != st.end()){
                    if((mp[*it]) <= v){
                        temp.insert(*it);
                        sol += (mp[*it] - *it -1 );
                        mp.erase(*it);
                        it++;
                    }
                    else {
                        break;
                    }
                }}
                sol -= (v-u-1);
                res.push_back(sol);
                for(auto &  x : temp){
                    st.erase(x);
                }
                mp[u] = v;
                st.insert(u);

        }
        return res;
    }
};
