class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i = 0; i < n; i++) {
            s.insert(i);
        }
        vector<int> ans;
        for(auto &q: queries) {
            auto it = s.lower_bound(q[0]+1);
            auto it2 = s.lower_bound(q[1]);
            s.erase(it,it2);
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
