class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> res;
        list<int> lis1(1, 0);
        unordered_map<int, list<int>::iterator> mp;
        for(int i = 1; i < n; i++) {
            lis1.push_back(i);
        }
        for(list<int>::iterator it = lis1.begin(); it != lis1.end(); it++) {
            mp[*it] = it;
        }
        int len = n;
        for(auto q : queries) {
            int l = q[0];
            int r = q[1];
            if(mp.count(l) == 0) {
                res.push_back(len - 1);
                continue;
            }
            list<int>::iterator it = mp[l];
            // cout << l << ' '  << r << endl;
            for(it++; it != lis1.end() && *it < r; ) {
                auto t_it = it;
                ++t_it;
                // cout << *it << endl;
                mp.erase(*it);
                lis1.erase(it);
                len--;
                it = t_it;
            }
            res.push_back(len - 1);
        }
        return res;
    }
};
