class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        int m = queries.length;
        int[] res = new int[m];
        TreeSet<Integer> set = new TreeSet<>();
        for(int i = 1; i < n; i ++){
            set.add(i);
        }
        int index = 0;
        for(int[] q : queries){
            while(true){
                Integer cur = set.ceiling(q[0] + 1);
                if(cur >= n - 1) break;
                if(cur >= q[1]) break;
                set.remove(cur);
            }
            res[index ++] = set.size();


        }



        return res;
    }
}
