class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        from sortedcontainers import SortedList
        sl = SortedList(range(n))
        res = []
        for u,v in queries:
            j = sl.bisect_left(v)
            if j == len(sl) or sl[j] != v:
                j -= 1
            i = sl.bisect_left(u+1)
            if i == len(sl) or sl[i] != u+1:
                res.append(len(sl)-1)
                continue
            for k in range(j, i, -1):
                sl.pop(k)
            res.append(len(sl)-1)
        return res
