#define ll long long
#define pb push_back
class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<pair<ll,ll>>st;
        ll curr = n-1;
        for(ll i=0;i<n-1;i++){
            st.insert({i,i+1});
        }
        vector<int>ans;
        for(ll i=0;i<q.size();i++){
            ll fir = q[i][0];
            ll sec = q[i][1];
            // for(auto it:st){
            //     cout<<it.first<<" "<<it.second<<endl;
            // }
            // cout<<endl;
            auto it = st.lower_bound({fir,0});
            bool pikaw = false;
            while(1){
                if(it!=st.end() && (*it).second<=sec){
                    auto it2 = it;
                    pikaw=true;
                     curr--;
                    it2++;
                    if(it2==st.end()){
                        st.erase(it);
                        break;
                    }

                    //What if it2 is end
                    ll fir2 = (*it2).first;
                    ll sec2 = (*it2).second;
                    st.erase(it);
                    auto it3 = st.find({fir2,sec2});
                    it = it3;
                }else{
                    break;
                }
            }
            if(pikaw){
                curr++;
                st.insert({fir,sec});
            }
            ans.pb(curr);
        }
        return ans;
    }
};
