from sortedcontainers import SortedList

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:

        sl = SortedList()
        for i in range(n-1):
            sl.add([i, i+1])

        ans = []
        #print(sl)

        for start, end in queries:
            idxl = sl.bisect_left([start, start])
            idxr = sl.bisect_right([end, end]) - 1
            if idxl < idxr:
                toRemove = []
                for k in range(idxl, idxr + 1):
                    toRemove.append(sl[k])
                for x in toRemove:
                    sl.remove(x)
                sl.add([start, end])

            ans.append(len(sl))

            #print(sl)

        return ans
