from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        L = SortedList(range(n))
        # print(L)
        ans = []
        for a, b in queries:
            left = 0
            right = len(L) - 1
            while left < right:
                middle = (left + right + 1) // 2
                if L[middle] <= a:
                    left = middle
                else:
                    right = middle - 1
            idx1 = left
            while L[idx1] == a and L[idx1 + 1] < b:
                L.remove(L[idx1 + 1])
            ans.append(len(L) - 1)
        return ans
