class Solution {
public:
    int f[100010], nn;
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& a) {
        nn = n;
        int res = n - 1;
        vector<int> ans;
        set<int> se;
        for(int i = 0; i < n; i ++)
        {
            se.insert(i);
            // f[i] = n - i - 1;
        }
        for(int i = 0; i < a.size(); i ++)
        {
            int x = a[i][0], y = a[i][1];
            auto it = se.upper_bound(x);
            if(*it >= y){
                ans.push_back(se.size() - 1);
                continue;
            }
            vector<int> v;
            while(1)
            {
                v.push_back(*it);
                it ++;
                if(*it >= y)break;
            }
            for(auto x : v)se.erase(x);
            ans.push_back(se.size() - 1);
        }
        return ans;
    }
};
