class Solution {
public:
  vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
    set<int> cities;
    for(int i = 0; i < n; i++) cities.insert(i);

    vector<int> res;
    for(auto &it : queries) {
      int u = it[0], v = it[1];
      for(int i = u + 1; i < v; i++) {
        auto it = cities.lower_bound(i);
        i = *it;
        if(*it < v) cities.erase(it);
      }
      res.push_back(cities.size() - 1);
    }
    return res;
  }
};
