class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        vector<int> ans;
        for(int i=0;i<n;i++) st.insert(i);
        for(auto p:queries){
            int l=min(p[0],p[1]);
            int r=max(p[0],p[1]);
            while(st.find(l)!=st.end()){
                auto vs=st.lower_bound(l+1);
                if(vs==st.end()) break;
                auto vs1=*vs;
                if(vs1>=r) break;
                st.erase(st.find(vs1));
            }
            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
