class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>&a) {
        set<int>st;
        int i;
        for(i=0;i<n;i++)
        {
            st.insert(i);
        }
        vector<int>ans(a.size());
        for(i=0;i<a.size();i++)
        {
            auto it=st.lower_bound(a[i][0]+1);
            auto it1=st.lower_bound(a[i][1]);
            st.erase(it,it1);
            ans[i]=st.size()-1;
        }
        return ans;
    }
};
