class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        stt = set([x for x in range(n)])
        removed = set()
        jumps = {}
        ret = []
        for st, ed in queries:
            if st in removed:
                ret.append(len(stt) - 1)
                continue
            i = st + 1
            while i < ed:
                if i in jumps:
                    i = jumps[i]

                removed.add(i)
                stt.discard(i)
                i += 1

            ret.append(len(stt) - 1)
            jumps[st + 1] = ed - 1

        return ret
