class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> answer, next(n - 1);
        int dist = n - 1;

        for (int i = 0; i < n - 1; ++i) {
            next[i] = i + 1;
        }

        for (auto& path : queries) {
            int from = path[0], to = path[1];
            if (to > next[from]) {
                int current = from, steps = 0;
                do  {
                    ++steps;
                    int tmp2 = next[current];
                    next[current] = INT_MAX;
                    current = tmp2;
                } while (current != to);
                next[from] = to;
                dist = dist - steps + 1;
            }

            // for (int i = 0; i < n - 1; ++i)
            //     printf("%d ", next[i]);
            // printf("\n");

            // printf("%d %d => %d\n", from , to, steps);
            answer.push_back(dist);
        }

        return answer;
    }
};
