class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ret;
        set<int> A;
        for (int i = 0; i < n; ++i) A.insert(i);
        for (auto& q : queries) {
            int u = q[0], v = q[1];
            auto L = A.lower_bound(u + 1);
            auto R = A.lower_bound(v);
            vector<int> V;
            for (auto it = L; it != R; ++it) V.push_back(*it);
                for (auto& x : V) A.erase(x);
            ret.push_back(A.size() - 1);
        }
        return ret;

    }
};
