class Solution {
    private static class Node {
        public int val;
        //public int distFromStart;
        //public int distToEnd;
        public boolean valid;
        public Node next;

        public Node(int v, int n) {
            val = v;
            //distFromStart = v;
            //distToEnd = n-1-v;
            valid = true;
        }
    }

    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        Node[] nodes = new Node[n];
        nodes[0] = new Node(0, n);
        for(int i=1; i<n; i++) {
            nodes[i] = new Node(i, n);
            nodes[i-1].next = nodes[i];
        }
        int total = n-1;
        int q = queries.length;
        int[] answer = new int[q];
        for(int i=0; i<q; i++) {
            int u = queries[i][0];
            int v = queries[i][1];
            if(nodes[u].valid && nodes[v].valid) {
                for(Node x = nodes[u].next; x.val!=v; x=x.next) {
                    x.valid = false;
                    total--;
                }
                nodes[u].next = nodes[v];
            }
            answer[i] = total;
        }
        return answer;
    }
}
