class Solution {
public:
    int pr[100010];
    int ne[100010];
    int find (int a)
    {
        if (pr[a] != a) pr[a] = find(pr[a]);
        return pr[a];
    }
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        for (int i = 0; i < n; i ++ ) {
            pr[i] = i;
            ne[i] = i + 1;
        }
        int num = n - 1;
        vector<int> v;
        for (int i = 0; i < queries.size(); i ++ ) {
            int a = queries[i][0], b = queries[i][1];
            int pa = find(a), pb = find(b);
            while (pa != pb && ne[pa] != pb) {
                pr[ne[a]] = pr[a];
                ne[a] = ne[ne[a]];
                num --;
            }
            v.push_back(num);
        }
        return v;
    }
};
