from typing import *
import sortedcontainers


class Solution:
    def shortestDistanceAfterQueries(
        self, n: int, queries: List[List[int]]
    ) -> List[int]:
        s = sortedcontainers.SortedList(range(n))
        res = []
        for u, v in queries:
            while True:
                i = s.bisect_right(u)
                if s[i] >= v:
                    break
                s.pop(i)
            res.append(len(s) - 1)
        return res
