constexpr int MAXN = 1e5 + 5;

set<int> st;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        st.clear();
        for (int i = 1; i <= n; i ++) {
            st.insert(i);
        }
        vector<int> vec;
        for (auto &q: queries) {
            int x = q[0] + 1, y = q[1] + 1;
            auto it = st.begin();
            while (!st.empty() && ((it = st.upper_bound(x)) != st.end())) {
                auto val = *it;
                if (val < y) {
                    st.erase(it);
                } else {
                    break;
                }
            }
            vec.push_back(st.size() - 1);
        }
        return vec;
    }
};
