class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        int ans=n-1;
        vector<int> res;
        map<int,int> mp;
        for(int i=0;i<n-1;i++) mp[i]=i+1;
        for(int i=0;i<q.size();i++){
            auto it=mp.lower_bound(q[i][0]);
            auto en=mp.lower_bound(q[i][1]);
            // cout<<it->first<<" "<<en->first<<endl;
            vector<int> key;
            for(auto x=it;x!=en;x++) key.push_back(x->first);
            for(auto x:key){
                mp.erase(x);
                ans--;
            }
            if(key.size()>0){
                ans++;
                mp[q[i][0]]=q[i][1];
            }
            res.push_back(ans);
        }
        return res;
    }
};
