class Solution:
    def search(self, path, num):
        l = 0
        r = len(path)
        while(r - l > 1):
            middle = (l+r)//2
            if num >= path[middle]:
                l = middle
            else:
                r = middle
        return l
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        dist = n-1
        result = []
        adj_max = [i+1 for i in range(n-1)]
        path = list(range(n))
        for q in queries:
            low = self.search(path, q[0])
            high = self.search(path, q[1])
            del path[low+1:high]
            result.append(len(path)-1)

        return result
