class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i=0;i<n;i++) s.insert(i);

        vector<int> ans;

        for(auto q : queries) {
            if(s.count(q[0]) > 0 && s.count(q[1]) > 0) {
                auto it = s.find(q[0]);
                while(it != s.end()) {
                    if(*it == q[1]) break;

                    if(*it!= q[0]) {
                        it = s.erase(it);
                    } else {
                        it++;
                    }
                }
            }

            ans.push_back(s.size() - 1);
        }

        return ans;
    }
};
