class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int>arr(n);
        vector<int>res;
        for (int i = 0; i < n; ++i)
            arr[i] = i;
        for (auto& i : queries)
        {
            int l = i[0];
            int r = i[1];
            auto ri = lower_bound(arr.begin(), arr.end(), r);
            auto li = lower_bound(arr.begin(), arr.end(), l);
            if (*li == l)li++;
            int ll = li - arr.begin();
            int val = *ri;
            for (int i = ll; *(arr.begin() + i) != val;)
                arr.erase(arr.begin() + i);
            res.push_back(arr.size()-1);
        }
        return res;
    }
};
