class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> graph;
        for (int i = 0; i < n; i++) {
            graph.insert(i);
        }
        vector<int> result;
        for (const auto &query : queries) {
            int u = query[0], v = query[1];
            auto start = graph.upper_bound(u);
            auto last = graph.lower_bound(v);
            graph.erase(start, last);
            result.push_back(graph.size() - 1);
        }
        return result;
    }
};
