function shortestDistanceAfterQueries(n: number, queries: number[][]): number[] {
    let next = Array.from({ length: n }, (_, i) => i + 1);
    let dist = n - 1;
    let used = new Set(Array.from({ length: n }, (_, i) => i));
    let ans = [];
    for (let [a, b] of queries) {
        if (!used.has(a) || b <= next[a]) {
            ans.push(dist);
            continue;
        }
        let d = 0;
        let c = next[a];
        while (c != b) {
            d++;
            used.delete(c);
            c = next[c];
        }
        next[a] = b;
        dist -= d;
        ans.push(dist);
    }
    return ans;
};
