class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        vector<int> ans;
        for (int i = 0; i < n; i++) {
            st.insert(i);
        }
        for (auto tt : queries) {
            int u = tt[0], v = tt[1];

            auto it = st.upper_bound(u);
            while (it != st.end() && *it < v) {
                // std::cout << *it << ' ';
                it = st.erase(it);
            }

            ans.push_back(st.size() - 1);

            // for (auto it : st) {
            //     std::cout << it << ' ';
            // }
            // std::cout << std::endl;
        }
        return ans;
    }
};
