class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> segments;
        for (int i = 1; i < n; ++i) {
            segments[i - 1] = i;
        }
        vector<int> ans;
        ans.reserve(queries.size());
        for (auto& q: queries) {
            auto it = prev(segments.upper_bound(q[0]));
            if (it->first == q[0]) {
                if (it->second < q[1]) {
                    it->second = q[1];
                    for (auto it2 = next(it); it2 != segments.end() && it2->first < q[1]; ) {
                        it2 = segments.erase(it2);
                    }
                }
            }
            ans.push_back(segments.size());
        }
        return ans;
    }
};
