class Solution {
public:
    void shutdown(int o,int left,int right,int mid,vector<vector<int>>&a){
        if(a[o][1]==true){
            a[o*2][0]=mid-left+1;
            a[o*2][1]=true;
            a[o*2+1][0]=right-mid;
            a[o*2+1][1]=true;
        }
    }
    void shutup(int o,vector<vector<int>>&a){
        a[o][0]=a[o*2][0]+a[o*2+1][0];
    }
    void set(int o,int left,int right,int L,int R,vector<vector<int>>&a){
        if(L<=left&&right<=R){
            a[o][0]=right-left+1;
            a[o][1]=true;
            return;
        }
        int mid=(left+right)/2;
        shutdown(o,left,right,mid,a);
        if(L<=mid){
            set(o*2,left,mid,L,R,a);
        }
        if(mid<R){
            set(o*2+1,mid+1,right,L,R,a);
        }
        shutup(o,a);
        return;
    }
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<vector<int>>a(4*n,vector<int>(2,0));
        vector<int>ans;
        for(auto& q:queries){
            set(1,1,n,q[0]+2,q[1],a);
            ans.push_back(n-a[1][0]-1);
        }
        return ans;
    }
};
