class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        next_dist = {}
        for i in range(n-1) :
            next_dist[i] = i+1

        to_ret = []
        need_visit = set(list(range(n)))
        for s, e in queries :
            if not s in need_visit or not e in need_visit:
                to_ret.append(len(need_visit)-1)
                continue
            # print(next_dist, need_visit)
            # print(s, e)
            while not next_dist[s] == e :
                need_visit.remove(next_dist[s])
                next_dist[s] = next_dist[next_dist[s]]

            to_ret.append(len(need_visit)-1)
        return to_ret
