class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:

        adjList = {}
        for i in range(n-1):
            adjList[i] = i + 1

        curValue = n - 1

        result = []

        for src, dst in queries:
            if src in adjList and adjList[src] < dst:
                cur = src
                size = 0
                while cur < dst:
                    nextCur = adjList[cur]
                    del adjList[cur]
                    cur = nextCur
                    size += 1
                adjList[src] = dst
                curValue -= size - 1
            result.append(curValue)
        return result
