int fa[100020];
class Solution {
public:
    int getfa(int x){
        if(fa[x] == x)return fa[x];
        fa[x] = getfa(fa[x]);
        return fa[x];
    }
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ret;
        int m = queries.size();
        for(int i=0;i<n;++i){
            fa[i] = i;
        }
        int nowans = n-1;
        for(int i=0;i<m;++i){
            int x = queries[i][0];
            int y = queries[i][1];
            int aug = 0;
            while(x < y){
                x = getfa(x);
                if(x < y-1) {
                    fa[x] = y-1;
                    ++ aug;
                    x ++;
                }else break;

            }
            nowans -= aug;
            ret.push_back(nowans);
        }
        return ret;
    }
};
