import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;
import java.util.Map;

class Solution {

    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {

        Node[] no = new Node[n];
        int cnt = n - 1;
        for (int i = 1; i <= n; i++) {
            Node temp = new Node();
            temp.range = new int[]{i - 1, i};
            no[i - 1] = temp;
            if (i > 1) {
                no[i - 2].next = no[i - 1];
            }
        }
        int[] res = new int[queries.length];
        for (int i = 0; i < queries.length; i++) {
            int[] q = queries[i];
            if (no[q[0]] != null && no[q[1]] != null) {
                Node pp = no[q[0]];
                Node end = no[q[1]];
                while (pp.next != end) {
                    no[pp.next.range[0]] = null;
                    pp.next = pp.next.next;
                    cnt--;
                }
            }
            res[i] = cnt;
        }
        return res;

    }

    public static void main(String[] args) {
        System.out.println(Arrays.toString(new Solution().shortestDistanceAfterQueries(5, new int[][]{
                {2, 4}, {0, 2}, {0, 4}
        })));
    }

    public static class Node {
        private Node next;
        private int[] range;
    }


}
