class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        vector<bool> check(n,1);
        vector<int> adj(n,0);
        for(int i=0;i<=n-2;i++){
            adj[i]=i+1;
        }
        int length=n-1;
        for(int i=0;i<queries.size();i++){
            if(check[queries[i][0]]==0 || check[queries[i][1]]==0){
                ans.push_back(length);
                continue;
            }
            int j=queries[i][0];
            int p=0;
            while(adj[j]!=queries[i][1]){
                // cout<<adj[j]<<endl;
                // if(p==10){
                //     break;
                // }
                check[adj[j]]=0;
                j=adj[j];
                p++;
            }
            length=length-p;
            ans.push_back(length);
            adj[queries[i][0]]=queries[i][1];
        }
        return ans;
    }
};
