class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int> s;
        vector<int> ans;
        for (int i = 0; i < n; i++) {
            s.insert(i);
        }
        int m = q.size();
        for (int i = 0; i < m; i++) {
            int l = q[i][0];
            int r = q[i][1];
            auto it = s.upper_bound(l); 
            while (it != s.end() && *it < r) {
                it = s.erase(it);
            }
            ans.push_back(s.size() - 1);
        }
        return ans;
    }
};
