class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> fwd(n);
        vector<bool> alive(n+1);
        for (int i = 0; i < n; i++) {
            fwd[i] = i+1;
            alive[i] = 1;
        }
        alive[n] = 1;
        int ans = n-1;
        vector<int> ret;
        for (vector<int>& x : queries) {
            int st = x[0];
            int ed = x[1];
            if (alive[st] && alive[ed]) {
                int pt = st;
                while (pt != ed) {
                    pt = fwd[pt];
                    if (pt != ed) alive[pt] = 0;
                    --ans;
                }
                fwd[st] = ed;
                ++ans;
            }
            ret.push_back(ans);
        }
        return ret;
    }
};
