class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<vector<int>>g(n);
        set<int>s;
        for(int i=0;i<n; i++)s.insert(i);
        vector<int>ans;


        for(auto &vec: q){
            int u= vec[0], v=vec[1];
            auto it= s.upper_bound(u);
            auto it1= s.lower_bound(v);
            vector<int>toErase;
            for(; it!=it1; it++)if(*it>u && *it<v)toErase.pb(*it);
            for(auto &x: toErase)s.erase(x);
            ans.pb((int)s.size()-1);
        }
        return ans;
    }
};
