class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> s;
        for(int i=0;i<n;i++){
            s.insert(i);
        }
        int ans = n - 1;
        vector<int> fin;
        for(auto& it: queries){
            int x = it[0];
            int y = it[1];
            auto start = s.upper_bound(x);
            auto end = s.lower_bound(y);

            ans -= distance(start, end);
            s.erase(start, end);

            fin.push_back(ans);
        }
        return fin;
    }
};
