class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int m = queries.size();
        vector<int> ans(m);

        vector<bool> vis(n,true);
        vector<int> g(n,n-1);
        for(int i=0;i<n-1;++i) g[i] = i+1;

        int dis = n-1;
        for(int i=0;i<m;++i){
            int u = queries[i][0] , v = queries[i][1];
            if(vis[u] && vis[v]){
                for(int x=g[u]; x!=v ; x = g[x]){
                    if(x==v) break;
                    vis[x] = false;
                    --dis;
                }
                g[u] = v;
            }
            ans[i] = dis;
        }
        return ans;
    }
};
