class LazySegmentTree:
    def __init__(self, data):
        self.n = len(data)
        self.tree = [0] * (4 * self.n)
        self.lazy = [None] * (4 * self.n)
        self.build(data, 0, 0, self.n - 1)

    def build(self, data, node, start, end):
        if start == end:
            self.tree[node] = data[start]
        else:
            mid = (start + end) // 2
            self.build(data, 2 * node + 1, start, mid)
            self.build(data, 2 * node + 2, mid + 1, end)
            self.tree[node] = self.tree[2 * node + 1] + self.tree[2 * node + 2]

    def update_range(self, l, r, value):
        self._update_range(0, 0, self.n - 1, l, r, value)

    def _update_range(self, node, start, end, l, r, value):
        if self.lazy[node] is not None:
            self._apply_lazy(node, start, end)

        if start > end or start > r or end < l:
            return

        if start >= l and end <= r:
            self.tree[node] = (end - start + 1) * value
            if start != end:
                self.lazy[2 * node + 1] = value
                self.lazy[2 * node + 2] = value
            return

        mid = (start + end) // 2
        self._update_range(2 * node + 1, start, mid, l, r, value)
        self._update_range(2 * node + 2, mid + 1, end, l, r, value)
        self.tree[node] = self.tree[2 * node + 1] + self.tree[2 * node + 2]

    def _apply_lazy(self, node, start, end):
        if self.lazy[node] is not None:
            self.tree[node] = (end - start + 1) * self.lazy[node]
            if start != end:
                self.lazy[2 * node + 1] = self.lazy[node]
                self.lazy[2 * node + 2] = self.lazy[node]
            self.lazy[node] = None

    def query_range(self, l, r):
        return self._query_range(0, 0, self.n - 1, l, r)

    def _query_range(self, node, start, end, l, r):
        if start > end or start > r or end < l:
            return 0

        if self.lazy[node] is not None:
            self._apply_lazy(node, start, end)

        if start >= l and end <= r:
            return self.tree[node]

        mid = (start + end) // 2
        left_query = self._query_range(2 * node + 1, start, mid, l, r)
        right_query = self._query_range(2 * node + 2, mid + 1, end, l, r)
        return left_query + right_query




class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:

        #[4,3,1,1,0]
        #[0,1,2,3,4]
        segtree = LazySegmentTree([1] * (n-1))
        ans = n-1
        res = []
        for t, f in queries:
            s = segtree.query_range(t, f-2)
            # print(s)
            ans -= s
            segtree.update_range(t, f-2, 0 )
            res.append(ans)

        return res
