class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) 
    {
        unordered_map <int,ListNode*> mp;

        for(int i=0;i<n;i++)
        {
            mp[i] = new ListNode(i);

            if(i != 0)
                mp[i-1]->next = mp[i]; 
        }

        vector <int> ans(queries.size());

        unordered_set <int> del_node;

        for(int i=0;i<queries.size();i++)
        {
            if(del_node.find(queries[i][0]) != del_node.end() || del_node.find(queries[i][1]) != del_node.end())
            {
                ans[i] = n - del_node.size() - 1;
                continue;
            }

            ListNode* current = mp[queries[i][0]]->next;

            while(current != mp[queries[i][1]])
            {
                ListNode* del = current;

                current = current->next;

                del_node.insert(del->val);

                del->next = NULL;

                delete(del);
            }

            mp[queries[i][0]]->next = mp[queries[i][1]];

            ans[i] = n - del_node.size() - 1;
        }

        return ans;
    }
};
