#define ll long long
class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int>arr(n,0);
        int m=q.size();
        vector<int>ends(n,-1);
        ends[n-1]=n+1;
        ll prev=n-1;
        vector<int>ans(m,0);
        for(int i=0;i<m;i++){
            int l=q[i][0];
            int r=q[i][1];
            ll len=0;
           // cout<<l<<"l"<<r<<"r"<<endl;
            for(int k=l;k<=r;k++){
                //cout<<k<<"k"<<endl;
                if(ends[k]!=-1){
                    len++;
                    int temp=ends[k];
                    ends[k]=max(ends[k],r);
                    k=max(temp-1,k);

                }
                else{
                    len++;
                    ends[k]=max(ends[k],r);

                }
            }
            // for(int j=0;j<n;j++){
            //     cout<<ends[j]<<" ";
            // }
            // cout<<endl;
            len-=2;
            len=max(len,0LL);
           // cout<<i<<" "<<len<<endl;
            ans[i]=prev-len;
            prev-=len;

        }
        return ans;
    }
};
