from sortedcontainers import SortedList, SortedSet, SortedDict 

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        nums = SortedList(list(range(n)))

        res = []

        for u, v in queries:

            first = nums.bisect_left(u)
            if nums[first] == u:
                first = nums.bisect_left(u+1)
            while nums[first] < v:
                curr = nums[first]
                del nums[first]
                first = nums.bisect_left(curr+1)


            res.append(len(nums)-1)

        return res
