class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> result;
        set<int> reachable;

        for (int i = 0; i < n; ++i) {
            reachable.insert(i);
        }

        for (const auto& query : queries) {
            int start = query[0];
            int end = query[1];
            auto it = reachable.upper_bound(start);
            while(*it < end && *it > start) {
                reachable.erase(it);
                it = reachable.upper_bound(start);
            }
            result.push_back(reachable.size()-1);
        }
        return result;
    }
};
